<?php

namespace Composer;

use Composer\Semver\VersionParser;






class InstalledVersions
{
private static $installed = array (
  'root' => 
  array (
    'pretty_version' => 'dev-develop',
    'version' => 'dev-develop',
    'aliases' => 
    array (
    ),
    'reference' => 'da82708ab7a4196cbffc881d0a121d00c0e90ed4',
    'name' => 'avfweb/avfcms',
  ),
  'versions' => 
  array (
    'avfweb/avfcms' => 
    array (
      'pretty_version' => 'dev-develop',
      'version' => 'dev-develop',
      'aliases' => 
      array (
      ),
      'reference' => 'da82708ab7a4196cbffc881d0a121d00c0e90ed4',
    ),
